import { Card, CardBody, CardHeader, Center, FormControl, Heading, Stack, useColorModeValue, Box, Input,
Progress } from "@chakra-ui/react";
import {Form, useForm} from "react-final-form"
import React from "react";


export default function Insert(){
    return(
        <Card align={'center'}>
            <CardHeader>
                <Heading size={'md'}>Create New Product</Heading>
            </CardHeader>
            <CardBody>
            <Center py={6}>
                <Stack borderWidth="1px" borderRadius="lg" w={{ sm: '100%', md: '740px' }}
                       height={{ sm: '476px', md: '25rem' }} direction={{ base: 'column', md: 'row' }}
                       bg={useColorModeValue('white', 'gray.900')} boxShadow={'2xl'} padding={4}>
                        <Form>
                        <Box
            as="form"
            p={4}
            borderWidth="1px"
            borderRadius="lg"
            boxShadow="1px 1px 3px rgba(0,0,0,0.3)"
            // onSubmit={handleSubmit}
          >
                        <Input name="firstName" label="First Name" />
                        <Input name="lastName" label="Last Name" />
                        <PercentComplete size="sm" my={2} hasStripe isAnimated />
            
                        </Box>
                        </Form>
                </Stack>

            </Center>
            </CardBody>

        </Card>
    )
}
const PercentComplete = props => {
    const form = useForm();
    const numFields = form.getRegisteredFields().length;
    const numErrors = Object.keys(form.getState().errors).length;
    return (
      <Progress
        value={numFields === 0 ? 0 : ((numFields - numErrors) / numFields) * 100}
        {...props}
      />
    );
  };
