import React from "react";
import {  Center, Stack, Flex, useColorModeValue, Heading, FormControl, FormLabel, Input, 
     InputRightElement, Button,FormHelperText, FormErrorMessage, InputGroup, ViewIcon, ViewOffIcon } from "@chakra-ui/react";
import { useState } from "react";
import { FaGhost } from "react-icons/fa";
export default function Register(){

        const [input, setInput] = useState('')
        const handleInputChange = (e) => setInput(e.target.value)
        const isError = input === ''
        const [showPassword, setShowPassword] = useState(false);
        const [show, setShow] = React.useState(false)
        const handleClick = () => setShow(!show)
        
    return(
        <Center py={6}>
            <Stack
            borderWidth="1px"
            borderRadius="lg"
            w={{ sm: '100%', md: '540px' }}
            height={{ sm: '476px', md: '20rem' }}
            direction={{ base: 'column', md: 'row' }}
            bg={useColorModeValue('white', 'gray.900')}
            boxShadow={'2xl'}
            padding={4}>     
                <FormControl>
                <Stack spacing={3}>
                <Heading fontSize={'2xl'} fontFamily={'body'}>
               <Center>Register User Account </Center> 
                </Heading>
                    <InputGroup>
                    <FormLabel>Username</FormLabel>
                    <Input type="username" value={input} onChange={handleInputChange} />
                    {!isError ?(
                        <FormHelperText> Enter username</FormHelperText>
                    ):(
                        <FormErrorMessage>Username is required</FormErrorMessage>
                    )}
                    </InputGroup>
                    <InputGroup>
                    <FormLabel>Password</FormLabel>
                    <Input pr='4.5rem' type={show ? 'text' : 'password'} placeholder='Enter password' />
                    <InputRightElement width='4.5rem'>
                    <Button
                    variant={FaGhost}
                    onClick={() =>
                      setShowPassword((showPassword) => !showPassword)
                    }>
                    {showPassword ? <ViewIcon /> : <ViewOffIcon />}
                  </Button>
                    </InputRightElement>
                    </InputGroup>
                </Stack>
                </FormControl>
            </Stack>
        </Center>
    )
}
