import React from "react";
import {  Center, Stack, Flex, useColorModeValue, Heading, FormControl, FormLabel, Input, FormHelperText, FormErrorMessage } from "@chakra-ui/react";
import { useState } from "react";
export default function Register(){

        const [input, setInput] = useState('')
      
        const handleInputChange = (e) => setInput(e.target.value)
      
        const isError = input === ''
        
    return(
        <Center py={6}>
            <Stack
            borderWidth="1px"
            borderRadius="lg"
            w={{ sm: '100%', md: '540px' }}
            height={{ sm: '476px', md: '20rem' }}
            direction={{ base: 'column', md: 'row' }}
            bg={useColorModeValue('white', 'gray.900')}
            boxShadow={'2xl'}
            padding={4}>
                <Flex flex={1}>     
                <Heading fontSize={'2xl'} fontFamily={'body'}>
                Regis User Account
                </Heading>
                </Flex>
                <Flex flex={1}>
                <FormControl>
                    <FormLabel>Username</FormLabel>
                    <Input type="username" value={input} onChange={handleInputChange} />
                    {!isError ?(
                        <FormHelperText> Enter username</FormHelperText>
                    ):(
                        <FormErrorMessage>Username is required</FormErrorMessage>
                    )}
                </FormControl>
                </Flex>
            </Stack>
        </Center>
    )
}
